fragment:
precision highp float;
precision highp int;

uniform vec2 start;
uniform int iter;
uniform vec3 color;
uniform float speed;
uniform float time;
uniform float escape;
varying vec2 vUv;

void main() {
    vec2 z;
    float t = time * speed;
    vec2 q = start + 0.1 * (
        vec2(
            sin(t * 0.89), cos(t)
        ) * (0.25 + 0.5 * sin(t * 1.108))
    ) - (
        vec2(
            cos(t * 0.39) + sin(t * .98)
        ) * (0.25 + 0.5 * cos(t * 1.91))
    );
    z.x = 3.0 * (vUv.x - .5);
    z.y = 3.0 * (vUv.y - .5);

    int y = 0;
    for (int i = 0; i < 100; i++) {
        y++;
        float x = (z.x * z.x - z.y * z.y) + q.x;
        float y = (z.x * z.y + z.x * z.y) + q.y;

        if ((x * x + y * y) > escape) {
            break;
        }
        z.x = x;
        z.y = y;
    }

    float val = float(y) / float(iter);
    gl_FragColor = vec4(color * val, 1.0) + 0.1;
}

vertex:
precision highp float;
precision highp int;

uniform mat4 modelViewMatrix;
uniform mat4 projectionMatrix;
varying vec2 vUv;

attribute vec3 position;
attribute vec2 uv;

void main() {
    vUv = uv;
    gl_Position = projectionMatrix * modelViewMatrix * vec4( position, 1.0 );
}
